function isDateValid(month,year){
    var fecha = new Date();
    var curYear = fecha.getFullYear();
    var curMonth = fecha.getMonth()+1;

    if(year>=curYear){
        if(year==curYear) {
            if(month>=curMonth){
                return true;
            } else {
                return false;
            }
        } else {
            return true;
        }
    } else {
        return false;
    }
}

